// --------------------------------------256x128-----------------------------------------------

TEST_F(MC_Yavta_Test, CI_TPG_IPU4_Yavta_Mipi_Capture_1_Frame_RAW8_256x128) {
    setup_tpg_mipi("fmt:SGRBG8/256x128");

    // yavta --data-prefix -B capture-mplane -u -c1 -n5 -I -s256x128 -F -f "SGRBG8" /dev/video6
    yavta_capture(true, true, true, 1, 5, true, 256, 128, true, "SGRBG8", "/dev/video6",
          false, false, STATS_IGNORE);

    verify_frame_mipi_tpg_256x128_grbg8(0);
}

TEST_F(MC_Yavta_Test, CI_TPG_IPU4_Yavta_Mipi_Capture_5_Frame_RAW8_256x128) {
    setup_tpg_mipi("fmt:SGRBG8/256x128");

    // yavta --data-prefix -B capture-mplane -u -c5 -n5 -I -s256x128 -F -f "SGRBG8" /dev/video6
    yavta_capture(true, true, true, 5, 5, true, 256, 128, true, "SGRBG8", "/dev/video6",
          false, false, STATS_IGNORE);

    for(int i=0; i<5; i++)
        verify_frame_mipi_tpg_256x128_grbg8(i);
}

